function updateImage() {
    const value = document.getElementById("beacon.overlay").value + document.getElementById("beacon.symbol").value;

    const image = document.querySelector("img");

    switch (value) {
        case "L&":
            image.src =
                "data:image/png;base64,";
            break;
        case "L#":
            image.src =
                "data:image/png;base64,";
            break;
        case "L_":
            image.src =
                "data:image/png;base64,";
            break;
        case "La":
            image.src =
                "data:image/png;base64,";
            break;
    }
}

